import React, { Component } from 'react'
import './index.css'
import { Input, Button, InputNumber, Select, message } from 'antd'
import { getAddressByZipCode } from '../../../../services/fornecedores'
import * as R from 'ramda'


const { Option } = Select;

class ReservaML extends Component{

  state={
    nomeProduto: '',
    quant: '1',
    carrinho: [],
    estoque: 'REALPONTO',
    codigo: '',
    nomeOuRs: '',
    cpfOuCnpj: '',
    cep: '',
    estado: '',
