import React, { Component } from 'react'
import './index.css'
import { Input, Button, Select, Modal, message } from 'antd'
import { masks } from './validators'
import { newTecnico, newCarro, getCarro } from '../../../../services/tecnico'


const { Option } = Select;

class NovoTecnico extends Component{

  state={
    carroArray:[],
    messageError: false,
    messageSuccess: false,
    nome: '',
    cnh: '',
    carro: '',
    rodizio: '',
    loading: false,
    modalCarro: false,
    newModelo: '',
    newPlaca: '',
    newAno: '',
  }

  getAllCarro = async () => {
    this.setState({
      loading: true,
    })

    await getCarro().then(
      resposta => this.setState({
        carroArray: resposta.data,
      })
    )

    this.setState({
      loading: false,
    })
  }

  success = () => {
    message.success('O cadastro foi efetuado');
  };

  error = () => {
    message.error('O cadastro não foi efetuado');
  };

  onChangeSelect = (value) => {
    this.setState({
      carro: value
    })

    this.rodizio()
  }

  rodizio = () => {
    if(this.state.carro[this.state.carro.length - 1] === '1' ){
      this.setState({
        rodizio: 'SEGUNDA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '2'  ){
      this.setState({
        rodizio: 'SEGUNDA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '3'  ){
      this.setState({
        rodizio: 'TERÇA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '4' ){
      this.setState({
        rodizio: 'TERÇA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '5' ){
      this.setState({
        rodizio: 'QUARTA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '6'  ){
      this.setState({
        rodizio: 'QUARTA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '7'  ){
      this.setState({
        rodizio: 'QUINTA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '8' ){
      this.setState({
        rodizio: 'QUINTA'
      })
    }else if(this.state.carro[this.state.carro.length - 1] === '9' ){
      this.setState({
        rodizio: 'SEXTA'
      })
    }
    else if(this.state.carro[this.state.carro.length - 1] === '0' ){
      this.setState({
        rodizio: 'SEXTA'
      })
    }
  }

  saveTargetNewFornecedor= async () => {

    this.setState({
      loading: true
    })

    const values = {
      name: this.state.nome,
      CNH: this.state.cnh,
      plate: this.state.placa,
    }

    const resposta = await newTecnico(values)

    console.log(resposta)

    if (resposta.status === 422) {

      this.setState({
        messageError: true,
        fieldFalha: resposta.data.fields[0].field,
        message: resposta.data.fields[0].message,
      })
      await this.error()
      this.setState({
        loading:false,
        messageError: false,
      })
    } if (resposta.status === 200) {

      this.setState({
        nome: '',
        cnh: '',
        placa: '',
        messageSuccess: true,
      })
      await this.success()
      this.setState({
        loading:false,
        messageSuccess: false
      })
    }
  }

  saveTargetNewCarro= async () => {

    this.setState({
      loading: true
    })

    const values = {
      model: this.state.newModelo,
      year: this.state.newAno,
      plate: this.state.newPlaca
    }

    const resposta = await newCarro(values)

    console.log(resposta)

    if (resposta.status === 422) {

      this.setState({
        messageError: true,
        fieldFalha: resposta.data.fields[0].field,
        message: resposta.data.fields[0].message,
      })
      await this.error()
      this.setState({
        loading:false,
        messageError: false,
      })
    } if (resposta.status === 200) {

      this.setState({
        newModelo: '',
        newPlaca: '',
        newAno: '',
        messageSuccess: true,
      })
      await this.success()
      this.setState({
        loading:false,
        messageSuccess: false,
        modalCarro: false
      })
    }

    this.getAllCarro()
  }

  openModal = () => {
    this.setState({
      modalCarro: true
    })
  }

  handleOk = () => {
    this.setState({
      modalCarro: false,
      newAno: '',
      newModelo: '',
      newPlaca: ''
    })
  }

  componentDidMount = async () => {
    await this.getAllCarro()
  }


  onChange = (e) => {
    const { nome,
      valor,
    } = masks(e.target.name, e.target.value)

    this.setState({
      [nome]: valor,
    })
  }

  onChangeNormal = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  modalCarro = () => (
    <Modal
      title="Adicionar carro"
      visible={this.state.modalCarro}
      onOk={this.saveTargetNewCarro}
      okText='Salvar'
      onCancel={this.handleOk}
      cancelText='Cancelar'
    >
      <div className='linhaModal-tecnico'>
        <div className='div-modeloModal-tecnico'>
          <div className='div-text-tecnico'>Modelo:</div>
          <Input
            className='input-100'
            placeholder="Digite o modelo"
            name='newModelo'
            value={this.state.newModelo}
            onChange={this.onChangeNormal}
            allowClear
          />
        </div>
      </div>

      <div className='linhaModal-tecnico'>
        <div className='div-anoModal-tecnico'>
          <div className='div-text-tecnico'>Ano:</div>
          <Input
            className='input-100'
            placeholder="2019"
            name='newAno'
            value={this.state.newAno}
            onChange={this.onChange}
            allowClear
          />
        </div>
      </div>

      <div className='linhaModal-tecnico'>
        <div className='div-placaModal-tecnico'>
          <div className='div-text-tecnico'>Placa:</div>
          <Input
            className='input-100'
            placeholder="ABC-1234"
            name='newPlaca'
            value={this.state.newPlaca}
            onChange={this.onChange}
            allowClear
          />
        </div>
      </div>
    </Modal>
  )

  render(){
    console.log(this.state)
    return(
      <div className='div-card-tecnico'>
        <div className='linhaTexto-tecnico'>
          <h1 className='h1-tecnico'>Técnico</h1>
        </div>

        <div className='linha1-tecnico'>
          <div className='div-nome-tecnico'>
            <div className='div-text-tecnico'>Nome:</div>
            <Input
              className='input-100'
              placeholder="Digite o nome"
              name='nome'
              value={this.state.nome}
              onChange={this.onChangeNormal}
              allowClear
            />
          </div>

          <div className='div-cnh-tecnico'>
            <div className='div-textCNH-tecnico'>Validade CNH:</div>
            <Input
              className='input-100'
              placeholder="DD/MM/AAAA"
              name='cnh'
              value={this.state.cnh}
              onChange={this.onChange}
              allowClear
            />
          </div>
        </div>

        <div className='linhaSemEspaco-tecnico'>
          <div className='div-carro-tecnico'>
            <div className='div-text-tecnico'>Carro:</div>
            {this.state.carroArray !== [] ? <Select defaultValue='Não selecionado' style={{ width: '100%' }} onChange={this.onChangeSelect}>
            {this.state.carroArray.map((valor) => 
            <Option value={valor.model + valor.plate}>{`${valor.model} ${valor.plate}`}</Option>)}</Select> :
            <Select value='Nenhum carro cadastrado' style={{ width: '100%' }}> </Select> }

          <Button className='buttonadd-marca-tecnico' type="primary" icon="plus" onClick={this.openModal} />
          </div>

          <div className='div-rodizio-tecnico'>
            <div className='div-text-tecnico'>Rodízio:</div>
            <Input
              readOnly
              className='input-100'
              name='rodizio'
              value={this.state.rodizio}
            />
            <this.modalCarro/>
          </div>
        </div>

        <div className='linha-button-tecnico'>
          <Button className='button' type="primary" onClick={this.saveTargetNewFornecedor} loading={this.state.loading}>Salvar</Button>
        </div>
      </div>
    )
  }
}

export default NovoTecnico
