import React, { Component } from 'react'
import './index.css'
import { Input } from 'antd'

class ReservaML extends Component{

  state={
    codigo: '',
    nomeOuRs: '',
    cpfOuCnpj: '',
    cep: '',
    estado: '',
    cidade: '',
    bairro: '',
    rua: '',
    numero: '',
    complemento: '',
    pontoReferencia: '',
    nomeContato: '',
    email: '',
    telefone: '',
    loading: false,
    fieldFalha: {
      cpfOuCnpj: false,
      razaoSocial: false,
      cep: false,
      estado: false,
      cidade: false,
      bairro: false,
      rua: false,
      numero: false,
      complemento: false,
      pontoReferencia: false,
      nomeContato: false,
      email: false,
      telefone: false,
    },
    message: {
      cpfOuCnpj: '',
      razaoSocial: '',
      cep: '',
      estado: '',
      cidade: '',
      bairro: '',
      rua: '',
      numero: '',
      complemento: '',
      pontoReferencia: '',
      nomeContato: '',
      email: '',
      telefone: '',
    }
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render(){
    return(
      <div className='div-card-ML'>
        <div className='linhaTexto-ML'>
          <h1 className='h1-ML'>Reserva mercado-livre</h1>
        </div>

        <div className='div-linha-ML'>
        <div className='div-codigo-ML'>
          <div className='div-textCodigo-ML'>Código de rastreio:</div>
            <Input
              className='input-100'
              style={{ width: '100%' }}
              name='Os'
              value={this.state.codigo}
              placeholder="Código de rastreio"
              onChange={this.onChange}
              // onBlur={this.onBlurValidator}
              allowClear
            />
          </div>

          <div className='div-nome-ML'>
          <div className='div-textNome-ML'>Nome ou razão social:</div>
            <Input
              className='input-100'
              style={{ width: '100%' }}
              name='Os'
              value={this.state.nomeOuRs}
              placeholder="Digite o nome ou a razão social"
              onChange={this.onChange}
              // onBlur={this.onBlurValidator}
              allowClear
            />
          </div>         
        </div>

        <div className='div-linha-ML'>
        <div className='div-cep-ML'>
            <div className='div-text-ML'>Cep:</div>
            {/* <div className='div-inputs'> */}
              <Input
                className={
                  this.state.fieldFalha.cep ?
                    'div-inputError' :
                    'input-100'}
                placeholder="Digite o cep"
                name='cep'
                // value={this.state.cep}
                onChange={this.onChange}
                allowClear
                onBlur={this.getAddress}
              />
              {/* {this.state.fieldFalha.cep ?
                <p className='div-feedbackError'>
                  {this.state.message.cep}
                </p> : null} */}
            {/* </div> */}
          </div>

          <div className='div-uf-ML'>
            <div className='div-text-ML'>UF:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.estado ?
                    'div-inputError' :
                    'input-100'}
                placeholder="EX"
                name='estado'
                value={this.state.estado}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.estado ?
                <p className='div-feedbackError'>
                  {this.state.message.estado}
                </p> : null}
            </div>
          </div>
        </div>

      </div>
    )
  }
}

export default ReservaML
