import React, { Component } from 'react'
import './index.css'
import { Input, Button, Select, Modal } from 'antd'
import { masks } from './validators'


const { Option } = Select;

class NovoTecnico extends Component{

  state={
    nome: '',
    cnh: '',
    carro: '',
    placa: '2530',
    rodizio: '',
    loading: false,
    modalCarro: false,
    newModelo: '',
    newPlaca: '',
    newAno: '',
  }

  rodizio = () => {
    if(this.state.placa[this.state.placa.length - 1] === 1 || 2){
      this.setState({
        rodizio: 'SEGUNDA'
      })
    }else if(this.state.placa[this.state.placa.length - 1] === 3 || 4){
      this.setState({
        rodizio: 'TERÃ‡A'
      })
    }if(this.state.placa[this.state.placa.length - 1] === 5 || 6){
      this.setState({
        rodizio: 'QUARTA'
      })
    }if(this.state.placa[this.state.placa.length - 1] === 7 || 8){
      this.setState({
        rodizio: 'QUINTA'
      })
    }if(this.state.placa[this.state.placa.length - 1] === 9 || 0){
      this.setState({
        rodizio: 'SEXTA'
      })
    }
  }

  openModal = () => {
    this.setState({
      modalCarro: true
    })
  }

  handleOk = () => {
    this.setState({
      modalCarro: false,
      newAno: '',
      newModelo: '',
      newPlaca: ''
    })
  }

  onChange = (e) => {
    const { nome,
      valor,
    } = masks(e.target.name, e.target.value)

    this.setState({
      [nome]: valor,
    })
  }

  // onChangeSelect = () => {
  //   this.setState({
  //     carro: 
  //   })
  // }

  modalCarro = () => (
    <Modal
      title="Adicionar carro"
      visible={this.state.modalCarro}
      onOk={this.handleOk}
      okText='Salvar'
      onCancel={this.handleOk}
      cancelText='Cancelar'
    >
      <div className='linhaModal-tecnico'>
        <div className='div-modeloModal-tecnico'>
          <div className='div-text-tecnico'>Modelo:</div>
          <Input
            className='input-100'
            placeholder="Digite o modelo"
            name='newModelo'
            value={this.state.newModelo}
            onChange={this.onChange}
            allowClear
          />
        </div>
      </div>

      <div className='linhaModal-tecnico'>
        <div className='div-anoModal-tecnico'>
          <div className='div-text-tecnico'>Ano:</div>
          <Input
            className='input-100'
            placeholder="2019"
            name='newAno'
            value={this.state.newAno}
            onChange={this.onChange}
            allowClear
          />
        </div>
      </div>

      <div className='linhaModal-tecnico'>
        <div className='div-placaModal-tecnico'>
          <div className='div-text-tecnico'>Placa:</div>
          <Input
            className='input-100'
            placeholder="ABC-1234"
            name='newPlaca'
            value={this.state.newPlaca}
            onChange={this.onChange}
            allowClear
          />
        </div>
      </div>
    </Modal>
  )

  render(){
    console.log(this.state)
    return(
      <div className='div-card-tecnico'>
        <div className='linhaTexto-tecnico'>
          <h1 className='h1-tecnico'>TÃ©cnico</h1>
        </div>

        <div className='linha1-tecnico'>
          <div className='div-nome-tecnico'>
            <div className='div-text-tecnico'>Nome:</div>
            <Input
              className='input-100'
              placeholder="Digite o nome"
              name='nome'
              value={this.state.nome}
              onChange={this.onChange}
              allowClear
            />
          </div>

          <div className='div-cnh-tecnico'>
            <div className='div-textCNH-tecnico'>Validade CNH:</div>
            <Input
              className='input-100'
              placeholder="DD/MM/AAAA"
              name='cnh'
              value={this.state.cnh}
              onChange={this.onChange}
              allowClear
            />
          </div>
        </div>

        <div className='linhaSemEspaco-tecnico'>
          <div className='div-carro-tecnico'>
            <div className='div-text-tecnico'>Carro:</div>
            <Select value='UP' style={{ width: '100%' }} >
              <Option value='UP' onChange={this.onChangeSelect}>UP AYD-6220</Option>
            </Select>
            {this.rodizio()}
          <Button className='buttonadd-marca-tecnico' type="primary" icon="plus" onClick={this.openModal} />
          </div>

          <div className='div-rodizio-tecnico'>
            <div className='div-text-tecnico'>RodÃ­zio:</div>
            <Input
              readOnly
              className='input-100'
              placeholder="Data do rodÃ­zio"
              name='rodizio'
              value={this.state.rodizio}
              allowClear
            />
            <this.modalCarro/>
          </div>
        </div>

        <div className='linha-button-tecnico'>
          <Button className='button' type="primary" loading={this.state.loading}>Salvar</Button>
        </div>
      </div>
    )
  }
}

export default NovoTecnico
