import React, { Component } from 'react'
import './index.css'
import { Input, Select, InputNumber, Button, message } from 'antd'
import { validators } from './validators'


const { Option } = Select;
const { TextArea } = Input;

const children = [];
for (let i = 10; i < 36; i++) {
  children.push(<Option key={i.toString(36) + i}>{i.toString(36) + i}</Option>);
}

class NovaEntrada extends Component {

  state = {
    messageError: false,
    messageSucesso: false,
    estoque: 'REALPONTO',
    nomeProduto: '',
    quant: '1',
    arrayProdutos: [],
    carrinho: [],
    fieldFalha: {
      nomeProduto: false,
    },
    message: {
      nomeProduto: ''
    },
  }

  success = () => {
    message.success('Entrada realizada com sucesso');
  };

  error = () => {
    message.error('Entrada não foi realizada com sucesso');
  };

  errorButton = () => {
    message.error('O nome do produto é obrigatório');
  };


  onChangeSelect = (value) => {
    this.setState({
      estoque: value
    })
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleChange = (value) => {
    this.setState({
      nomeProduto: value
    })
  }

  onChangeQuant = (value) => {
    this.setState({
      quant: value
    })
  }

  onBlurValidator = (e) => {
    const {
      nome,
      valor,
      fieldFalha,
      message
    } = validators(e.target.name, e.target.value, this.state)

    this.setState({
      [nome]: valor,
      fieldFalha,
      message
    })
  }

  add = async () => {

    await this.setState({
      carrinho: [{
        nomeProdutoCarrinho: this.state.nomeProduto,
        quantCarrinho: this.state.quant
      }, ...this.state.carrinho]
    })

    await this.setState({
      nomeProduto: '',
      quant: '1'
    })
  }

  remove = (value) => {
    const oldCarrinho = this.state.carrinho
    const newCarrinho = oldCarrinho.filter(valor => valor !== value)

    this.setState({
      carrinho: newCarrinho
    })
  }

  render() {
    return (
      <div className='div-card-entrada'>
        <div className='linhaTexto-entrada'>
          <h1 className='h1-entrada'>Nova entrada</h1>
        </div>

        <div className='linha1-entrada'>
          <div className='div-nome-entrada'>
            <div className='div-textNome-entrada'>Nome do produto:</div>
            <div className='div-inputs'>
            <Input
            className={
              this.state.fieldFalha.nomeProduto ?
                'div-inputError' :
                'input-100'}
              style={{ width: '100%' }}
              name='nomeProduto'
              value={this.state.nomeProduto}
              placeholder="Digite o nome do produto"
              onChange={this.onChange}
              onBlur={this.onBlurValidator}
            />
            {this.state.fieldFalha.nomeProduto ?
              <p className='div-feedbackError'>
                {this.state.message.nomeProduto}
              </p> : null}
              </div>
          </div>

          <div className='div-estoque-entrada'>
            <div className='div-text-entrada'>Estoque:</div>
            <Select value={this.state.estoque} style={{ width: '100%' }} onChange={this.onChangeSelect} >
              <Option value='REALPONTO'>REALPONTO</Option>
              <Option value='NOVA REALPONTO'>NOVA REALPONTO</Option>
              <Option value='PONTOREAL'>PONTOREAL</Option>
            </Select>
          </div>
        </div>

        <div className='linha1-entrada'>
          <div className='div-nome-entrada'>
            <div className='div-text-entrada'>Fornecedor:</div>
            <div className='div-inputs'>
            <Input
            className={
              this.state.fieldFalha.fornecedor ?
                'div-inputError' :
                'input-100'}
              style={{ width: '100%' }}
              name='nomeProduto'
              value={this.state.fornecedor}
              placeholder="Digite o fornecedor"
              onChange={this.onChange}
              onBlur={this.onBlurValidator}
            />
            {this.state.fieldFalha.fornecedor ?
              <p className='div-feedbackError'>
                {this.state.message.fornecedor}
              </p> : null}
              </div>
          </div>

          <div className='div-quant-entrada'>
            <div className='div-text-entrada'>Quant:</div>
            <InputNumber min={1} defaultValue={this.state.quant} value={this.state.quant} onChange={this.onChangeQuant} />
          </div>

          {this.state.nomeProduto === '' ? 
          <Button
            type='primary'
            className='button'
            onClick={this.errorButton}
          >
            Adicionar
          </Button> :  
          <Button
            type='primary'
            className='button'
            onClick={this.add}
          >
            Adicionar
            </Button>
          }
        </div>

        <div className='linha1-entrada'>
          <h2 className='h4-entrada'>Produtos selecionados</h2>
        </div>

        {this.state.carrinho.length === 0 ? null : this.state.carrinho.map((valor) =>
          <div className='linha1-entrada'>
            <h1 className='h1-produtos-entrada'>{valor.nomeProdutoCarrinho}</h1>
            <h1 className='h1-produtos-entrada'>{valor.quantCarrinho} UN</h1>
            <Button type='primary' className='button-remove-entrada' onClick={() => this.remove(valor)}>Remover</Button>
          </div>)}

        <div className='linha-button-entrada'>
          <Button className='button' type="primary" loading={this.state.loading}>Salvar</Button>
        </div>

      </div>
    )
  }
}

export default NovaEntrada
