import React, { Component } from 'react'
import './index.css'
import { Input, Select } from 'antd'


const { Option } = Select;

class AddKit extends Component{

  state={
    item: '',
    quant: '',
    estoque: 'REALPONTO',
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  onChangeSelect = (value) => {
    this.setState({
      estoque: value
    })
  }

  render(){
    return(
      <div className='div-card-AddKit'>
        <div className='linhaTexto-AddKit'>
          <h1 className='h1-AddKit'>Gerenciar kit</h1>
        </div>

          <div className='linha1-AddKit'>
          <div className='div-nome-AddKit'>
            <div className='div-text-AddKit'>Item:</div>
            <Input
              className='input-100'
              placeholder="Digite o nome"
              name='item'
              value={this.state.item}
              onChange={this.onChangeNormal}
              allowClear
            />
          </div>

          <div className='div-quant-AddKit'>
            <div className='div-text-AddKit'>Quant:</div>
            <Input
              className='input-100'
              placeholder="123465"
              name='quant'
              value={this.state.quant}
              onChange={this.onChange}
              allowClear
            />
          </div>

          <div className='div-estoque-AddKit'>
            <div className='div-text-AddKit'>Estoque:</div>
            <Select value={this.state.estoque} style={{ width: '100%' }} onChange={this.onChangeSelect} >
              <Option value='REALPONTO'>REALPONTO</Option>
              <Option value='NOVA REALPONTO'>NOVA REALPONTO</Option>
              <Option value='PONTOREAL'>PONTOREAL</Option>
            </Select>
          </div>
        </div>
      </div>
    )
  }
}

export default AddKit
