import React, { Component } from 'react'
import './index.css'
import { Input } from 'antd'

class ReservaML extends Component{

  state={
    codigo: '',
    nomeOuRs: '',
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  render(){
    return(
      <div className='div-card-ML'>
        <div className='linhaTexto-ML'>
          <h1 className='h1-ML'>Reserva mercado-livre</h1>
        </div>

        <div className='div-linha-ML'>
        <div className='div-codigo-ML'>
          <div className='div-textCodigo-ML'>Código de rastreio:</div>
            <Input
              className='input-100'
              style={{ width: '100%' }}
              name='Os'
              value={this.state.codigo}
              placeholder="Código de rastreio"
              onChange={this.onChange}
              // onBlur={this.onBlurValidator}
              allowClear
            />
          </div>

          <div className='div-nome-ML'>
          <div className='div-textNome-ML'>Nome ou razão social:</div>
            <Input
              className='input-100'
              style={{ width: '100%' }}
              name='Os'
              value={this.state.nomeOuRs}
              placeholder="Digite o nome ou a razão social"
              onChange={this.onChange}
              // onBlur={this.onBlurValidator}
              allowClear
            />
          </div>

          <div className='linha1-fornecedor'>
          <div className='div-cep-fornecedor'>
            <div className='div-text-fornecedor'>Cep:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.cep ?
                    'div-inputError' :
                    'input-100'}
                placeholder="Digite o cep"
                name='cep'
                value={this.state.cep}
                onChange={this.onChange}
                allowClear
                onBlur={this.getAddress}
              />
              {this.state.fieldFalha.cep ?
                <p className='div-feedbackError'>
                  {this.state.message.cep}
                </p> : null}
            </div>
          </div>

          <div className='div-uf-fornecedor'>
            <div className='div-text-fornecedor'>UF:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.estado ?
                    'div-inputError' :
                    'input-100'}
                placeholder="EX"
                name='estado'
                value={this.state.estado}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.estado ?
                <p className='div-feedbackError'>
                  {this.state.message.estado}
                </p> : null}
            </div>
          </div>

          <div className='div-cidade-fornecedor'>
            <div className='div-text-fornecedor'>Cidade:</div>
            <div className='div-inputs'>
              <Input
                className={
                this.state.fieldFalha.cidade ?
                  'div-inputError' :
                  'input-100'}
                placeholder="Digite a cidade"
                name='cidade'
                value={this.state.cidade}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.cidade ?
                <p className='div-feedbackError'>
                  {this.state.message.cidade}
                </p> : null}
            </div>
          </div>
        </div>


        <div className='linha1-fornecedor'>
          <div className='div-bairro-fornecedor'>
            <div className='div-text-fornecedor'>Bairro:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.bairro ?
                    'div-inputError' :
                    'input-100'}
                placeholder="Digite o bairro"
                name='bairro'
                value={this.state.bairro}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.bairro ?
                <p className='div-feedbackError'>
                  {this.state.message.bairro}
                </p> : null}
            </div>
          </div>

          <div className='div-rua-fornecedor'>
            <div className='div-text-fornecedor'>Rua:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.rua ?
                    'div-inputError' :
                    'input-100'}
                placeholder="Digite a rua"
                name='rua'
                value={this.state.rua}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.rua ?
                <p className='div-feedbackError'>
                  {this.state.message.rua}
                </p> : null}
            </div>
          </div>

          <div className='div-n-fornecedor'>
            <div className='div-text-fornecedor'>Nº:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.numero ?
                    'div-inputError' :
                    'input-100'}
                placeholder="123456"
                name='numero'
                value={this.state.numero}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.numero ?
                <p className='div-feedbackError'>
                  {this.state.message.numero}
                </p> : null}
            </div>
          </div>
        </div>

        <div className='linha1-fornecedor'>
          <div className='div-comp-fornecedor'>
            <div className='div-text-fornecedor'>Compl:</div>
            <div className='div-inputs'>
              <Input
                className='input-100'
                placeholder="Digite o complemento"
                name='complemento'
                value={this.state.complemento}
                onChange={this.onChange}
                allowClear
              />
            </div>
          </div>

          <div className='div-ref-fornecedor'>
            <div className='div-textRef-fornecedor'>Ponto de r ef:</div>
            <div className='div-inputs'>
              <Input
                className='input-100'
                placeholder="Digite a referência"
                name='pontoReferencia'
                value={this.state.pontoReferencia}
                onChange={this.onChange}
                allowClear
              />
            </div>
          </div>
        </div>

        <div className='linha1-fornecedor'>
          <div className='div-nome-fornecedor'>
            <div className='div-text-fornecedor'>Nome:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.nomeContato ?
                    'div-inputError' :
                    'input-100'}
                placeholder="Digite o nome"
                name='nomeContato'
                value={this.state.nomeContato}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.nomeContato ?
                <p className='div-feedbackError'>
                  {this.state.message.nomeContato}
                </p> : null}
            </div>
          </div>

          <div className='div-email-fornecedor'>
            <div className='div-text-fornecedor'>Email:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.email ?
                    'div-inputError' :
                    'input-100'}
                placeholder="Digite o email"
                name='email'
                value={this.state.email}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.email ?
                <p className='div-feedbackError'>
                  {this.state.message.email}
                </p> : null}
            </div>
          </div>

          <div className='div-tel-fornecedor'>
            <div className='div-text-fornecedor'>Telefone:</div>
            <div className='div-inputs'>
              <Input
                className={
                  this.state.fieldFalha.telefone ?
                    'div-inputError' :
                    'input-100'}
                placeholder="(11) 95771-2340"
                name='telefone'
                value={this.state.telefone}
                onChange={this.onChange}
                allowClear
                onBlur={this.onBlurValidator}
              />
              {this.state.fieldFalha.telefone ?
                <p className='div-feedbackError'>
                  {this.state.message.telefone}
                </p> : null}
            </div>
          </div>
        </div>

        </div>
      </div>
    )
  }
}

export default ReservaML
