import React, { Component } from 'react'
import './index.css'
import { Input, Button } from 'antd';
import { validators, masks } from './validators'
import * as R from 'ramda'
import { getAddressByZipCode, newFornecedor } from '../../../../services/fornecedores'

class NovoFornecedor extends Component {

  state = {
    cnpj: '',
    razaoSocial: '',
    cep: '',
    estado: '',
    cidade: '',
    bairro: '',
    rua: '',
    numero: '',
    complemento: '',
    pontoReferencia: '',
    nomeContato: '',
    email: '',
    telefone: '',
    loading: false,
    fieldFalha: {
      cnpj: false,
      razaoSocial: false,
      cep: false,
      estado: false,
      cidade: false,
      bairro: false,
      rua: false,
      numero: false,
      complemento: false,
      pontoReferencia: false,
      nomeContato: false,
      email: false,
      telefone: false,
    },
    message: {
      cnpj: '',
      razaoSocial: '',
      cep: '',
      estado: '',
      cidade: '',
