import React, { Component } from 'react'
import './index.css'
import { Input, Select, InputNumber, Button } from 'antd'


const { Option } = Select;
const { TextArea } = Input;

const children = [];
for (let i = 10; i < 36; i++) {
  children.push(<Option key={i.toString(36) + i}>{i.toString(36) + i}</Option>);
}

class NovaEntrada extends Component {

  state = {
    descricao: '',
    estoque: 'REALPONTO',
    nomeProduto: '',
    quant: '',
    arrayProdutos: ['teste1', 'teste2']
  }

  onChangeSelect = (value) => {
    this.setState({
      estoque: value
    })
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }


  render() {
    return (
      <div className='div-card-entrada'>
        <div className='linhaTexto-entrada'>
          <h1 className='h1-entrada'>Nova entrada</h1>
        </div>

        <div className='linha1-entrada'>
          <div className='div-descricao-entrada'>
            <div className='div-text-entrada'>Descrição:</div>
            <TextArea
              className='input-100'
              placeholder="Digite a descrição"
              autosize={{ minRows: 2, maxRows: 4 }}
              name='descricao'
              rows={4}
              value={this.state.descricao}
              onChange={this.onChange}
            />
          </div>

          <div className='div-estoque-entrada'>
            <div className='div-text-entrada'>Estoque:</div>
            <Select value={this.state.estoque} style={{ width: '100%' }} onChange={this.onChangeSelect} >
              <Option value='REALPONTO'>REALPONTO</Option>
              <Option value='NOVA REALPONTO'>NOVA REALPONTO</Option>
              <Option value='PONTOREAL'>PONTOREAL</Option>
            </Select>
          </div>
        </div>
        <div className='div-separeteLine-entrada'></div>

        <div className='linhaTexto-entrada'>
          <h1 className='h1-entrada'>Produtos da compra</h1>
        </div>

        <div className='linha1-entrada'>
          <div className='div-nome-entrada'>
            <div className='div-textNome-entrada'>Nome do produto:</div>
            <Select mode="tags" style={{ width: '100%' }} placeholder="Digite o nome do produto" onChange={this.handleChange}>
              {children}
            </Select>
          </div>

          <div className='div-quant-entrada'>
            <div className='div-text-entrada'>Quant:</div>
            <InputNumber min={1} defaultValue={1} name='quant' onChange={this.onChange} />
          </div>

          <Button type='primary' className='button'>Adicionar</Button>
        </div>

        <div className='linha1-entrada'>
          <h2 className='h4-entrada'>Produtos selecionados:</h2>
        </div>
      </div>
    )
  }
}

export default NovaEntrada
