import React, { Component } from 'react'
import { Input, Select, Radio, Button, Modal } from 'antd'
import './index.css'


const { Option } = Select;
const { TextArea } = Input;

class NovoProduto extends Component {

  state = {
    item: '',
    categoria: 'Equipamento',
    marca: '',
    tipo: '',
    modelo: '',
    descricao: '',
    codigo: '',
    quantMin: '',
    possui: false,
    quantEstoque: '',
    modalMarca: false,
    modalTipo: false,
    modalModelo: false,
  }

  handleOk = () => {
    this.setState({
      modalMarca: false,
    });
  };

  handleCancel = () => {
    this.setState({
      modalMarca: false,
    });
  };

  openModalMarca = () => {
    this.setState({
      modalMarca: true,
    })
  }

  handleChange = (value) => {
    this.setState({
      categoria: value
    })
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  changeRadioSim = () => {
    this.setState({
      possui: true
    })
  }

  changeRadioNao = () => {
    this.setState({
      possui: false,
      quantEstoque: ''
    })
  }

  modalMarca = () => (
    <Modal
      title="Adicionar marca"
      visible={this.state.modalMarca}
      onOk={this.handleOk}
      okText='Salvar'
      onCancel={this.handleOk}
      cancelText='Cancelar'
    >
      <div className='linhaModal-produtos'>
          <div className='div-marcaModal-produtos'>
            <div className='div-text-produtos'>Marca:</div>
            <Input
              className='input-100'
              placeholder="Digite a marca"
              name='marca'
              value={this.state.item}
              onChange={this.onChange}
              allowClear
            />
          </div>
      </div>

      <div className='linhaModal-produtos'>
          <div className='div-fabricanteModal-produtos'>
            <div className='div-text-produtos'>Fabricante:</div>
            <Input
              className='input-100'
              placeholder="Digite o fabricante"
              name='fabricante'
              value={this.state.fabricante}
              onChange={this.onChange}
              allowClear
            />
          </div>
      </div>
    </Modal>
  )

  render() {
    return (
      <div className='div-card-produtos'>
        <div className='linhaTexto-produtos'>
          <h1 className='h1-produtos'>Produtos</h1>
        </div>

        <div className='linha1-produtos'>
          <div className='div-item-produtos'>
            <div className='div-text-produtos'>Item:</div>
            <Input
              className='input-100'
              placeholder="Digite o item"
              name='item'
              value={this.state.item}
              onChange={this.onChange}
              allowClear
            />
          </div>

          <div className='div-categoria-produtos'>
            <div className='div-text-produtos'>Categoria:</div>
            <Select value={this.state.categoria} style={{ width: '100%' }} onChange={this.handleChange}>
              <Option value='Equipamento'>Equipamento</Option>
              <Option value='Peca'>Peca</Option>
              <Option value='Outros'>Outros</Option>
            </Select>
          </div>

          <div className='div-marca-produtos'>
            <div className='div-text-produtos'>Marca:</div>
            <Input
              className='input-100'
              placeholder="Digite a marca"
              name='marca'
              value={this.state.marca}
              onChange={this.onChange}
              allowClear
            />
            <Button className='buttonadd-marca-produtos' type="primary" icon="plus" onClick={this.openModalMarca} />
          </div>
          <this.modalMarca/>
        </div>

        {this.state.categoria === 'Equipamento' ? <div className='linha1-produtos'>
          <div className='div-tipo-produtos'>
            <div className='div-text-produtos'>Tipo:</div>
            <Input
              className='input-100'
              placeholder="Digite o tipo"
              name='tipo'
              value={this.state.tipo}
              onChange={this.onChange}
              allowClear
            />
            <Button className='buttonadd-marca-produtos' type="primary" icon="plus" />
          </div>

          <div className='div-modelo-produtos'>
            <div className='div-text-produtos'>Modelo:</div>
            <Input
              className='input-100'
              placeholder="Digite o modelo"
              name='modelo'
              value={this.state.modelo}
              onChange={this.onChange}
              allowClear
            />
            <Button className='buttonadd-marca-produtos' type="primary" icon="plus" />
          </div>

        </div> : null}

        <div className='linha1-produtos'>
          <div className='div-descricao-produtos'>
            <div className='div-text-produtos'>Descrição:</div>
            <TextArea
              className='input-100'
              placeholder="Digite a descrição"
              autosize={{ minRows: 2, maxRows: 4 }}
              rows={4}
              value={this.state.descricao}
              onChange={this.onChange}
            />
          </div>
        </div>

        <div className='linha1-produtos'>
          <div className='div-codigo-produtos'>
            <div className='div-text-produtos'>Código:</div>
            <Input
              className='input-100'
              placeholder="12345"
              name='codigo'
              value={this.state.codigo}
              onChange={this.onChange}
              allowClear
            />
          </div>

          <div className='div-quantMin-produtos'>
            <div className='div-textQuant-produtos'>Quant. min:</div>
            <Input
              className='input-100'
              placeholder="12345"
              name='quantMin'
              value={this.state.quantMin}
              onChange={this.onChange}
              allowClear
            />
          </div>

          <div className='div-possui-produtos'>
            <div className='div-textQuant-produtos'>Possui no estoque?</div>
            <Radio.Group name="radiogroup" value={this.state.possui}>
              <Radio value={true} onChange={this.changeRadioSim}>Sim</Radio>
              <Radio value={false} onChange={this.changeRadioNao}>Não</Radio>
            </Radio.Group>
            <div className='div-quantEstoque-produtos'>
              {this.state.possui === true ? <Input
                className='input-100'
                placeholder='12345'
                name='quantEstoque'
                value={this.state.quantEstoque}
                onChange={this.onChange}
              /> : null}
            </div>
          </div>
        </div>

        <div className='linha-button-produtos'>
          <Button type="primary" loading={this.state.loading}>Salvar</Button>
        </div>

      </div>
    )
  }
}

export default NovoProduto
