        message.cep = 'Cep não encontrado.'

        this.setState({
          fieldFalha,
          message,
        })
      } else {
        this.setState({
          rua: address.data.logradouro,
          cidade: address.data.localidade,
          bairro: address.data.bairro,
          estado: address.data.uf,
